import { defineConfig, presets, Sponsorship } from 'sponsorkit'

export const companys = {
  'marvin-robot': {
    url: 'https://www.prefect.io/',
    logo: 'data:image/svg+xml;base64,',
  },
  'Leniolabs': {
    url: 'https://www.leniolabs.com/',
    logo: 'data:image/png;base64,',
  },
  'programmierbar': {
    url: 'https://www.programmier.bar/',
    logo: 'data:image/jpeg;base64,',
  },
  'nuxt': {
    url: 'https://nuxtjs.org/',
    logo: 'data:image/svg+xml;base64,',
  },
};

export default defineConfig({
  onSponsorsFetched(sponsors) {
    // defineTierForSponsorInPast(sponsors, 'programmierbar', 1000, new Date(2022, 5, 25, 0, 0, 0, 0).valueOf());
  },
  customComposer(composer, sponsors, config) {

    let remainSponsors = [...sponsors];

    composer.height = 40;

    const tiers = [
      {
        name: 'The Platinum Sponsor',
        sponsors: consumeSponsors(sponsor => sponsor.monthlyDollars >= 2000),
        preset: presets.xl,
        logoSize: {
          width: 688,
          height: 160,
        },
      },
      {
        name: 'Gold Sponsors',
        sponsors: consumeSponsors(sponsor => sponsor.monthlyDollars >= 400),
        preset: presets.large,
        logoSize: {
          width: 342,
          height: 114,
        },
      },
      {
        name: 'Silver Sponsors',
        sponsors: consumeSponsors(sponsor => sponsor.monthlyDollars >= 100),
        preset: presets.medium,
        logoSize: {
          width: 226,
          height: 84,
        },
      },
    ];

    for (const tier of tiers) {
      const companySponsors = tier.sponsors.filter(sponsor => !!companys[sponsor.sponsor.login]);
      if (companySponsors.length) {
        composer.addTitle(tier.name);
        addCompanySponsors(companySponsors, tier.logoSize.width, tier.logoSize.height);
        composer.height += 64;
      }
    }

    // for (const tier of tiers) {
    //   const nonCompanySponsors = tier.sponsors.filter(sponsor => !companys[sponsor.sponsor.login]);
    //   if (nonCompanySponsors.length) {
    //     composer.addTitle(tier.name);
    //     composer.addSponsorGrid(nonCompanySponsors, tier.preset);
    //     composer.height += 32;
    //   }
    // }

    function consumeSponsors(filter: (sponsor: typeof sponsors[number]) => boolean) {
      const sponsorsToAdd = remainSponsors.filter(filter);
      remainSponsors = remainSponsors.filter(sponsor => !sponsorsToAdd.includes(sponsor));
      return sponsorsToAdd;
    }
    function addCompanySponsors(sponsorsToAdd: typeof sponsors, width: number, height: number) {

      const baseLeft = 56;
      const contentWidth = config.width! - baseLeft * 2;
      const maxNumsOfLine = Math.floor(contentWidth / width);
      const sponsorsWithCompany = sponsorsToAdd.map(sponsor => companys[sponsor.sponsor.login] ? { sponsor, company: companys[sponsor.sponsor.login] } : undefined).filter(sponsor => !!sponsor);

      for (let i = 0; i < sponsorsWithCompany.length; i += maxNumsOfLine) {

        const add = sponsorsWithCompany.slice(i, Math.min(sponsorsWithCompany.length, i + maxNumsOfLine));
        const length = 1 / add.length;
        const step = length / 2;

        for (let j = 0; j < add.length; j++) {
          const svg = `<a xlink:href="${add[j].company.url}" class="sponsor-link" target="_blank" id="${add[j].sponsor.sponsor.login}">
            <image x="${baseLeft + (step + j * length) * contentWidth - width / 2}" y="${composer.height}" width="${width}" height="${height}" xlink:href="${add[j].company.logo}"/>
          </a>`;
          composer.addRaw(svg);
        }

        composer.height += height;
        composer.height += 5;
      }
    }
  },
})

function defineTierForSponsorInPast(sponsors: Sponsorship[], username: string, tier: number, expire: number) {

  if (Date.now() > expire)
    return;

  let sponsor = sponsors.find(sponsor => sponsor.sponsor.login === username);
  if (sponsor) {
    sponsor.monthlyDollars = Math.max(sponsor.monthlyDollars, tier);
  }
  else {
    sponsors.push({
      createdAt: '',
      isOneTime: true,
      monthlyDollars: 1000,
      privacyLevel: 'PUBLIC',
      sponsor: {
        name: username,
        login: username,
        type: 'Organization',
        avatarUrl: ''
      },
      tierName: '',
    })
  }
}
